import { useState } from "react";
import "./App.css";

function App() {
  const [serviceList, setServiceList] = useState([{ title: "", description: "" }]);

  const handleServiceChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...serviceList];
    list[index][name] = value;
    setServiceList(list);
  };

  const handleServiceRemove = (index) => {
    const list = [...serviceList];
    list.splice(index, 1);
    setServiceList(list);
  };

  const handleServiceAdd = () => {
    setServiceList([...serviceList, { title: "", description: "" }]);
  };

  const urlCreator = (text) => {

    var site = text.replace(" ", "%20")
    return site
  }

  return (
    <form className="App" autoComplete="off">
      <div className="form-field">
        <label htmlFor="service">Video(s)</label>
        {serviceList.map((singleService, index) => (
          <div key={index} className="services">
            <div className="first-division">
              <input
                name="title"
                type="text"
                id="title"
                value={singleService.title}
                onChange={(e) => handleServiceChange(e, index)}
                required
              />
              <input
                name="description"
                type="text"
                id="description"
                value={singleService.description}
                onChange={(e) => handleServiceChange(e, index)}
                required
              />
              {serviceList.length - 1 === index && serviceList.length < 4 && (
                <button
                  type="button"
                  onClick={handleServiceAdd}
                  className="add-btn"
                >
                  <span>Add a Service</span>
                </button>
              )}
            </div>
            <div className="second-division">
              {serviceList.length !== 1 && (
                <button
                  type="button"
                  onClick={() => handleServiceRemove(index)}
                  className="remove-btn"
                >
                  <span>Remove</span>
                </button>
              )}
            </div>
          </div>
        ))}
      </div>
      <div className="output">
        <h2>Output</h2>
        {serviceList &&
          serviceList.map((singleService, index) => (
            <ul key={index}>
              {singleService.title &&  <li>Title: {singleService.title}</li>}
              <ul>
                {singleService.description && <li>Description: {singleService.description}</li>}
                {<li>URL: https://hpedemoportal.ext.hpe.com/{urlCreator(singleService.title)}</li>}

              </ul>
            </ul>
          ))}
      </div>
    </form>
  );
}

export default App;
